let myValue = "";

function show() {
    let input = document.getElementById("myInput");
    let div = document.getElementById("allHobbies");
    if (input.value.length > 0) {
        div.innerHTML += (input.value + "<br>");
        myValue += input.value + ",";
        document.getElementsByName("hidden")[0].value = myValue;
        input.value = "";
    }
}

function toTitleCase(str) {
    return str.replace(/\w\S*/g, function (txt) {
        return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase();
    });
}

function addInfo() {
    let xmlHttp = new XMLHttpRequest();
    xmlHttp.open("POST", "services/moreInfo.php", false);
    let formData = new FormData(document.querySelector("#more_info"));
    xmlHttp.send(formData);
    /* processing response */
    if (xmlHttp.readyState === 4) {
        if (xmlHttp.status === 200) {
            let results = xmlHttp.responseText;
            alert(results);
            if (results === "success") {
                location.reload();
            } else {
                if (results.includes("Duplicate")) {
                    addAlert("Username is used, please try again");
                }
                return false;
            }
        }
    }
    return false;
}
